import React, {Component} from 'react';
import axios from 'axios';

import { getColor } from 'utils/colors';
import { randomNum } from 'utils/demos';

import { Row, Col, Card, CardHeader, CardBody } from 'reactstrap';

import {Line} from 'react-chartjs-2';
import 'chartjs-plugin-streaming';

import Page from 'components/Page';

class ChartPage extends Component{
   ComponentDidMount(){
    if (!this.props.Component)createGist();
   }
   render(){
      return ChartPage_ret()
   }
}

const MONTHS = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];

const getData = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:5000/result');
      console.log('got Something2')
      return res.data
    } catch (err) {
      alert('Oh hey. Something went wrong...');
    }
}

function createGist() {
  fetch('http://127.0.0.1:5000/result', {
      method: 'get',
    }).then(function(response) {
      console.log('got Something')
      return response.json();
    }).then(function(data) {
      console.log(data)
      return data
    });
}

const genLineData = (moreData = {}, moreData2 = {}) => {
  return {
    labels: MONTHS,
    datasets: [
      {
        label: 'Dataset 1',
        backgroundColor: getColor('primary'),
        borderColor: getColor('primary'),
        borderWidth: 1,
        data: [],
        ...moreData,
      },
      {
        label: 'Dataset 2',
        backgroundColor: getColor('secondary'),
        borderColor: getColor('secondary'),
        borderWidth: 1,
        data: [],
        ...moreData2,
      },
    ],
  };
};

const options = {
  scales: {
    xAxes: [
      {
        type: "realtime",
        realtime: {
          onRefresh: function(chart) {
            chart.data.datasets.forEach(function(dataset) {
              dataset.data.push({
                x: Date.now(),
                y: createGist()
              });
            });
          },
          delay: 2000
        }
      }
    ]
  }
};

const ChartPage_ret = () => {
  return (
    <Page title="Charts" breadcrumbs={[{ name: 'Charts', active: true }]}>
      <Row>
        <Col xl={6} lg={12} md={12}>
          <Card>
            <CardHeader>Stacked Line</CardHeader>
            <CardBody>
              <Line
                data={genLineData()}
                options={options}
              />
            </CardBody>
          </Card>
        </Col>

        <Col xl={6} lg={12} md={12}>
          <Card>
            <CardHeader>Line</CardHeader>
            <CardBody>
              <Line 
                data={genLineData({ fill: false }, { fill: false })} 
                options={options}
              />
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Page>
  );
};

export default ChartPage;
